import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [cityA, setCityA] = useState('');
  const [cityB, setCityB] = useState('');
  const [weather, setWeather] = useState(null);

  const getWeather = async () => {
    if (!cityA || !cityB) {
      alert("Please enter both cities.");
      return;
    }

    try {
      const response = await axios.get('http://localhost:4000/api/weather', {
        params: { cityA, cityB }
      });
      setWeather(response.data);
    } catch (error) {
      console.error("Error fetching weather:", error);
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial, sans-serif' }}>
      <h1>Compare Weather</h1>

      <div style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          placeholder="Enter City A"
          value={cityA}
          onChange={(e) => setCityA(e.target.value)}
          style={{ padding: '0.5rem', marginRight: '1rem' }}
        />
        <input
          type="text"
          placeholder="Enter City B"
          value={cityB}
          onChange={(e) => setCityB(e.target.value)}
          style={{ padding: '0.5rem', marginRight: '1rem' }}
        />
        <button onClick={getWeather} style={{ padding: '0.5rem 1rem' }}>
          Get Weather
        </button>
      </div>

    {weather && (
    <div>
        <h2>Results</h2>
        <p>{cityA}: {weather.cityA.temp}째C / {((weather.cityA.temp * 9) / 5 + 32).toFixed(1)}째F</p>
        <p>{cityB}: {weather.cityB.temp}째C / {((weather.cityB.temp * 9) / 5 + 32).toFixed(1)}째F</p>
    </div>
    )}

    </div>
  );
}

export default App;
