import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [cityA, setCityA] = useState('');
  const [cityB, setCityB] = useState('');
  const [weather, setWeather] = useState(null);

  const getWeather = async () => {
    if (!cityA || !cityB) {
      alert("Please enter both cities.");
      return;
    }

    try {
      const response = await axios.get('http://localhost:4000/api/weather', {
        params: { cityA, cityB }
      });
      setWeather(response.data);
    } catch (error) {
      console.error("Error fetching weather:", error);
    }
  };
    return (
        <div
        style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            minHeight: '100vh',
            fontFamily: 'Arial, sans-serif',
            padding: '2rem',
            backgroundImage: 'url("/clouds.jpg")',  
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat',
            color: '#333',
        }}
        >
        <div
        style={{
            backgroundColor: 'rgba(255, 255, 255, 0.8)', // semi-transparent white
            padding: '1rem 2rem',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
            marginBottom: '1rem',
            textAlign: 'center',
        }}
        >
        <h1 style={{ color: '#00796b', margin: 0 }}>Compare the Weather (Live Data)</h1>
        </div>

        <div style={{ marginBottom: '1rem' }}>
        <input
            type="text"
            placeholder="Enter City A"
            value={cityA}
            onChange={(e) => setCityA(e.target.value)}
            style={{
            padding: '0.5rem',
            marginRight: '1rem',
            borderRadius: '4px',
            border: '1px solid #00796b',
            backgroundColor: '#ffffff',
            }}
        />
        <input
            type="text"
            placeholder="Enter City B"
            value={cityB}
            onChange={(e) => setCityB(e.target.value)}
            style={{
            padding: '0.5rem',
            marginRight: '1rem',
            borderRadius: '4px',
            border: '1px solid #00796b',
            backgroundColor: '#ffffff',
            }}
        />
        <button
            onClick={getWeather}
            style={{
            padding: '0.5rem 1rem',
            backgroundColor: '#00796b',
            color: '#ffffff',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            }}
        >
            Get Weather
        </button>
        </div>

        {weather && (
        <div
            style={{
            backgroundColor: '#ffffff',
            padding: '1rem 2rem',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            textAlign: 'center',
            }}
        >
            <h2 style={{ color: '#00796b' }}>Results</h2>
            <p>
            {cityA}: {weather.cityA.temp}째C / {((weather.cityA.temp * 9) / 5 + 32).toFixed(1)}째F
            </p>
            <p>
            {cityB}: {weather.cityB.temp}째C / {((weather.cityB.temp * 9) / 5 + 32).toFixed(1)}째F
            </p>
        </div>
        )}
    </div>
    );
}

export default App;
