import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [cityCount, setCityCount] = useState(2);
  const [cities, setCities] = useState(['', '']);
  const [weatherData, setWeatherData] = useState([]);

  const handleCityChange = (index, value) => {
    const updated = [...cities];
    updated[index] = value;
    setCities(updated);
  };

    const handleCityCountChange = (e) => {
    const count = parseInt(e.target.value);
    if (isNaN(count) || count < 1 || count > 10) return;

    setCityCount(count);
    setCities(Array(count).fill(''));
    setWeatherData([]);
    };

  const getWeather = async () => {
    const results = [];

    for (const city of cities) {
      if (!city) continue;
      try {
        const response = await axios.get('https://api.openweathermap.org/data/2.5/weather', {
          params: {
            q: city,
            units: 'metric',
            appid: 'c75c981860a96ab6063e0ae97da59e40',
          },
        });
        const tempC = response.data.main?.temp;
        results.push({
          city,
          tempC,
          tempF: ((tempC * 9) / 5 + 32).toFixed(1),
        });
      } catch (error) {
        results.push({ city, error: 'Not found' });
      }
    }

    setWeatherData(results);
  };

  return (
    <div
      style={{
        backgroundImage: 'url("/clouds.jpg")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        padding: '2rem',
        fontFamily: 'Arial, sans-serif',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }}
    >
      <div
        style={{
          backgroundColor: 'rgba(255,255,255,0.8)',
          padding: '1rem 2rem',
          borderRadius: '8px',
          marginBottom: '1rem',
          textAlign: 'center',
        }}
      >
        <h1 style={{ color: '#00796b', margin: 0 }}>Compare Weather</h1>
      </div>

      <div style={{ marginBottom: '1rem', textAlign: 'center' }}>
        <label style={{ marginRight: '1rem' }}>How many cities?</label>
        <input
          type="number"
          min="1"
          max="10"
          value={cityCount}
          onChange={handleCityCountChange}
          style={{ padding: '0.5rem', width: '60px' }}
        />
      </div>

      <div style={{ marginBottom: '1rem' }}>
        {cities.map((city, index) => (
          <input
            key={index}
            type="text"
            placeholder={`City ${index + 1}`}
            value={city}
            onChange={(e) => handleCityChange(index, e.target.value)}
            style={{
              padding: '0.5rem',
              marginRight: '0.5rem',
              marginBottom: '0.5rem',
              borderRadius: '4px',
              border: '1px solid #00796b',
            }}
          />
        ))}
      </div>

      <button
        onClick={getWeather}
        style={{
          padding: '0.5rem 1rem',
          backgroundColor: '#00796b',
          color: '#fff',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
          marginBottom: '1rem',
        }}
      >
        Get Weather
      </button>

      {weatherData.length > 0 && (
        <div
          style={{
            backgroundColor: 'rgba(255,255,255,0.9)',
            padding: '1rem 2rem',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            textAlign: 'center',
          }}
        >
          <h2 style={{ color: '#00796b' }}>Results</h2>
          {weatherData.map((entry, index) => (
            <p key={index}>
              {entry.city}: {entry.error ? entry.error : `${entry.tempC}°C / ${entry.tempF}°F`}
            </p>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;


